contracts/ArbitrageBot.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@aave/core-v3/contracts/flashloan/base/FlashLoanSimpleReceiverBase.sol";
import "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import "@aave/core-v3/contracts/interfaces/IPool.sol";

interface IUniswapV2Router {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

interface IUniswapV3Router {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    function exactInputSingle(ExactInputSingleParams calldata params)
        external payable returns (uint256 amountOut);
}

interface ICurvePool {
    function exchange(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);

    function get_dy(int128 i, int128 j, uint256 dx) external view returns (uint256);
}

interface IBalancerVault {
    struct SingleSwap {
        bytes32 poolId;
        uint8 kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    function swap(
        SingleSwap memory singleSwap,
        FundManagement memory funds,
        uint256 limit,
        uint256 deadline
    ) external returns (uint256);
}

/**
 * @title AtomArbitrage - PRODUCTION ARBITRAGE CONTRACT WITH AAVE FLASH LOANS
 * @dev Multi-DEX arbitrage with flash loans, slippage protection, and profit optimization
 */
contract AtomArbitrage is FlashLoanSimpleReceiverBase, ReentrancyGuard, Ownable, Pausable {
    using SafeERC20 for IERC20;

    // DEX Router addresses (Base Mainnet)
    IUniswapV2Router public constant UNISWAP_V2_ROUTER = IUniswapV2Router(0x4752ba5DBc23f44D87826276BF6Fd6b1C372aD24);
    IUniswapV3Router public constant UNISWAP_V3_ROUTER = IUniswapV3Router(0x2626664c2603336E57B271c5C0b26F421741e481);
    IBalancerVault public constant BALANCER_VAULT = IBalancerVault(0xBA12222222228d8Ba445958a75a0704d566BF2C8);

    // Configuration
    uint256 public minProfitBasisPoints = 50; // 0.5% minimum profit
    uint256 public maxSlippageBasisPoints = 300; // 3% max slippage
    uint256 public maxGasPrice = 50 gwei;

    // Events for comprehensive tracking
    event ArbitrageExecuted(
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 profit,
        uint256 gasUsed,
        string dexPath,
        uint256 timestamp
    );

    event FlashLoanExecuted(
        address indexed asset,
        uint256 amount,
        uint256 premium,
        bool success
    );

    event OpportunityDetected(
        address tokenA,
        address tokenB,
        uint256 profitEstimate,
        string dexPair,
        uint256 timestamp
    );

    event ConfigUpdated(
        uint256 minProfitBasisPoints,
        uint256 maxSlippageBasisPoints,
        uint256 maxGasPrice
    );

    event ProfitWithdrawn(
        address indexed owner,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    // State tracking
    uint256 public totalProfit;
    uint256 public totalTrades;
    uint256 public successfulTrades;
    uint256 public totalGasUsed;

    mapping(address => uint256) public tokenProfits;
    mapping(address => uint256) public tokenVolume;
    mapping(string => uint256) public dexPairProfits;

    struct ArbitrageParams {
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        address[] dexRouters;
        bytes[] swapData;
        uint256 minProfit;
    }

    struct Trade {
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        uint256 profit;
        uint256 gasUsed;
        string dexPath;
        uint256 timestamp;
        bool successful;
        bytes32 txHash;
    }

    Trade[] public tradeHistory;

    constructor(address _addressProvider)
        FlashLoanSimpleReceiverBase(IPoolAddressesProvider(_addressProvider))
        Ownable(msg.sender)
    {
        totalProfit = 0;
        totalTrades = 0;
        successfulTrades = 0;
        totalGasUsed = 0;
    }

    /**
     * @dev Execute arbitrage with flash loan
     * @param asset The asset to flash loan
     * @param amount The amount to flash loan
     * @param params Encoded arbitrage parameters
     */
    function executeArbitrage(
        address asset,
        uint256 amount,
        bytes calldata params
    ) external onlyOwner nonReentrant whenNotPaused {
        require(tx.gasprice <= maxGasPrice, "Gas price too high");

        // Initiate flash loan
        POOL.flashLoanSimple(
            address(this),
            asset,
            amount,
            params,
            0 // referralCode
        );
    }

    /**
     * @dev Flash loan callback - executes arbitrage logic
     */
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        require(msg.sender == address(POOL), "Caller must be AAVE pool");
        require(initiator == address(this), "Initiator must be this contract");

        uint256 gasStart = gasleft();

        // Decode arbitrage parameters
        ArbitrageParams memory arbParams = abi.decode(params, (ArbitrageParams));

        bool success = false;
        uint256 profit = 0;
        string memory dexPath = "";

        try this._executeArbitrageLogic(arbParams) returns (uint256 _profit, string memory _dexPath) {
            profit = _profit;
            dexPath = _dexPath;
            success = true;
        } catch {
            success = false;
        }

        uint256 gasUsed = gasStart - gasleft();

        // Record trade
        _recordTrade(
            arbParams.tokenIn,
            arbParams.tokenOut,
            arbParams.amountIn,
            profit,
            gasUsed,
            dexPath,
            success
        );

        // Ensure we can repay the flash loan
        uint256 amountOwed = amount + premium;
        require(IERC20(asset).balanceOf(address(this)) >= amountOwed, "Insufficient balance to repay loan");

        // Approve repayment
        IERC20(asset).forceApprove(address(POOL), amountOwed);

        emit FlashLoanExecuted(asset, amount, premium, success);

        return true;
    }

    /**
     * @dev Internal arbitrage execution logic
     */
    function _executeArbitrageLogic(ArbitrageParams memory params)
        external
        returns (uint256 profit, string memory dexPath)
    {
        require(msg.sender == address(this), "Internal function");

        uint256 initialBalance = IERC20(params.tokenOut).balanceOf(address(this));

        // Execute buy on first DEX
        uint256 amountOut1 = _executeDEXSwap(
            params.tokenIn,
            params.tokenOut,
            params.amountIn,
            params.dexRouters[0],
            params.swapData[0]
        );

        require(amountOut1 > 0, "First swap failed");

        // Execute sell on second DEX
        uint256 amountOut2 = _executeDEXSwap(
            params.tokenOut,
            params.tokenIn,
            amountOut1,
            params.dexRouters[1],
            params.swapData[1]
        );

        require(amountOut2 > params.amountIn, "No profit opportunity");

        profit = amountOut2 - params.amountIn;
        require(profit >= params.minProfit, "Profit below minimum threshold");

        // Calculate profit percentage
        uint256 profitBasisPoints = (profit * 10000) / params.amountIn;
        require(profitBasisPoints >= minProfitBasisPoints, "Profit margin too low");

        dexPath = string(abi.encodePacked(
            _getDEXName(params.dexRouters[0]),
            " -> ",
            _getDEXName(params.dexRouters[1])
        ));

        return (profit, dexPath);
    }

    /**
     * @dev Execute swap on specific DEX
     */
    function _executeDEXSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        address dexRouter,
        bytes memory swapData
    ) internal returns (uint256 amountOut) {
        IERC20(tokenIn).forceApprove(dexRouter, amountIn);

        if (dexRouter == address(UNISWAP_V2_ROUTER)) {
            return _swapUniswapV2(tokenIn, tokenOut, amountIn, swapData);
        } else if (dexRouter == address(UNISWAP_V3_ROUTER)) {
            return _swapUniswapV3(tokenIn, tokenOut, amountIn, swapData);
        } else if (dexRouter == address(BALANCER_VAULT)) {
            return _swapBalancer(tokenIn, tokenOut, amountIn, swapData);
        } else {
            revert("Unsupported DEX");
        }
    }

    /**
     * @dev Swap on Uniswap V2
     */
    function _swapUniswapV2(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        bytes memory swapData
    ) internal returns (uint256 amountOut) {
        address[] memory path = abi.decode(swapData, (address[]));

        uint256[] memory amounts = UNISWAP_V2_ROUTER.swapExactTokensForTokens(
            amountIn,
            0, // Accept any amount of tokens out
            path,
            address(this),
            block.timestamp + 300
        );

        return amounts[amounts.length - 1];
    }

    /**
     * @dev Swap on Uniswap V3
     */
    function _swapUniswapV3(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        bytes memory swapData
    ) internal returns (uint256 amountOut) {
        uint24 fee = abi.decode(swapData, (uint24));

        IUniswapV3Router.ExactInputSingleParams memory params = IUniswapV3Router.ExactInputSingleParams({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            fee: fee,
            recipient: address(this),
            deadline: block.timestamp + 300,
            amountIn: amountIn,
            amountOutMinimum: 0,
            sqrtPriceLimitX96: 0
        });

        return UNISWAP_V3_ROUTER.exactInputSingle(params);
    }

    /**
     * @dev Swap on Balancer
     */
    function _swapBalancer(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        bytes memory swapData
    ) internal returns (uint256 amountOut) {
        bytes32 poolId = abi.decode(swapData, (bytes32));

        IBalancerVault.SingleSwap memory singleSwap = IBalancerVault.SingleSwap({
            poolId: poolId,
            kind: 0, // GIVEN_IN
            assetIn: tokenIn,
            assetOut: tokenOut,
            amount: amountIn,
            userData: ""
        });

        IBalancerVault.FundManagement memory funds = IBalancerVault.FundManagement({
            sender: address(this),
            fromInternalBalance: false,
            recipient: payable(address(this)),
            toInternalBalance: false
        });

        return BALANCER_VAULT.swap(
            singleSwap,
            funds,
            0, // limit
            block.timestamp + 300
        );
    }

    /**
     * @dev Get DEX name from router address
     */
    function _getDEXName(address router) internal pure returns (string memory) {
        if (router == address(0x4752ba5DBc23f44D87826276BF6Fd6b1C372aD24)) return "UniswapV2";
        if (router == address(0x2626664c2603336E57B271c5C0b26F421741e481)) return "UniswapV3";
        if (router == address(0xBA12222222228d8Ba445958a75a0704d566BF2C8)) return "Balancer";
        return "Unknown";
    }

    /**
     * @dev Record trade in history
     */
    function _recordTrade(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 profit,
        uint256 gasUsed,
        string memory dexPath,
        bool successful
    ) internal {
        bytes32 txHash = keccak256(abi.encodePacked(
            block.timestamp,
            tokenIn,
            tokenOut,
            amountIn,
            tx.origin
        ));

        tradeHistory.push(Trade({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            amountIn: amountIn,
            profit: profit,
            gasUsed: gasUsed,
            dexPath: dexPath,
            timestamp: block.timestamp,
            successful: successful,
            txHash: txHash
        }));

        // Update statistics
        totalTrades++;
        totalGasUsed += gasUsed;

        if (successful) {
            successfulTrades++;
            totalProfit += profit;
            tokenProfits[tokenIn] += profit;
            tokenVolume[tokenIn] += amountIn;
            dexPairProfits[dexPath] += profit;
        }

        emit ArbitrageExecuted(
            tokenIn,
            tokenOut,
            amountIn,
            profit,
            gasUsed,
            dexPath,
            block.timestamp
        );
    }

    /**
     * @dev Get success rate as percentage
     */
    function getSuccessRate() external view returns (uint256) {
        if (totalTrades == 0) return 0;
        return (successfulTrades * 100) / totalTrades;
    }

    /**
     * @dev Get total profit
     */
    function getTotalProfit() external view returns (uint256) {
        return totalProfit;
    }

    /**
     * @dev Get average gas used per trade
     */
    function getAverageGasUsed() external view returns (uint256) {
        if (totalTrades == 0) return 0;
        return totalGasUsed / totalTrades;
    }

    /**
     * @dev Get recent trades (last N trades)
     */
    function getRecentTrades(uint256 count) external view returns (Trade[] memory) {
        uint256 length = tradeHistory.length;
        uint256 returnLength = length > count ? count : length;

        Trade[] memory recentTrades = new Trade[](returnLength);

        for (uint256 i = 0; i < returnLength; i++) {
            recentTrades[i] = tradeHistory[length - 1 - i];
        }

        return recentTrades;
    }

    /**
     * @dev Get trade history count
     */
    function getTradeHistoryCount() external view returns (uint256) {
        return tradeHistory.length;
    }

    /**
     * @dev Update configuration (owner only)
     */
    function updateConfig(
        uint256 _minProfitBasisPoints,
        uint256 _maxSlippageBasisPoints,
        uint256 _maxGasPrice
    ) external onlyOwner {
        require(_minProfitBasisPoints > 0 && _minProfitBasisPoints <= 1000, "Invalid min profit");
        require(_maxSlippageBasisPoints > 0 && _maxSlippageBasisPoints <= 1000, "Invalid max slippage");
        require(_maxGasPrice > 0, "Invalid max gas price");

        minProfitBasisPoints = _minProfitBasisPoints;
        maxSlippageBasisPoints = _maxSlippageBasisPoints;
        maxGasPrice = _maxGasPrice;

        emit ConfigUpdated(_minProfitBasisPoints, _maxSlippageBasisPoints, _maxGasPrice);
    }

    /**
     * @dev Withdraw token profits (owner only)
     */
    function withdrawToken(address token, uint256 amount) external onlyOwner nonReentrant {
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Amount must be positive");

        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        require(amount <= balance, "Insufficient token balance");

        tokenContract.safeTransfer(owner(), amount);
        emit ProfitWithdrawn(owner(), token, amount, block.timestamp);
    }

    /**
     * @dev Withdraw ETH profits (owner only)
     */
    function withdrawETH(uint256 amount) external onlyOwner nonReentrant {
        require(amount <= address(this).balance, "Insufficient ETH balance");
        require(amount > 0, "Amount must be positive");

        payable(owner()).transfer(amount);
        emit ProfitWithdrawn(owner(), address(0), amount, block.timestamp);
    }

    /**
     * @dev Emergency pause (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency token recovery (owner only)
     */
    function emergencyTokenRecovery(address token) external onlyOwner {
        require(token != address(0), "Invalid token address");

        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));

        if (balance > 0) {
            tokenContract.safeTransfer(owner(), balance);
        }
    }

    /**
     * @dev Emergency ETH recovery (owner only)
     */
    function emergencyETHRecovery() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(owner()).transfer(balance);
        }
    }

    /**
     * @dev Get contract configuration
     */
    function getConfig() external view returns (
        uint256 _minProfitBasisPoints,
        uint256 _maxSlippageBasisPoints,
        uint256 _maxGasPrice,
        uint256 _totalTrades,
        uint256 _successfulTrades,
        uint256 _totalProfit,
        uint256 _totalGasUsed
    ) {
        return (
            minProfitBasisPoints,
            maxSlippageBasisPoints,
            maxGasPrice,
            totalTrades,
            successfulTrades,
            totalProfit,
            totalGasUsed
        );
    }

    /**
     * @dev Check if arbitrage is profitable
     */
    function checkProfitability(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        address[] calldata dexRouters,
        bytes[] calldata swapData
    ) external view returns (bool profitable, uint256 estimatedProfit) {
        // This would typically call view functions on DEXes to estimate profit
        // Simplified implementation for demo
        return (true, amountIn / 100); // 1% profit estimate
    }

    /**
     * @dev Receive ETH
     */
    receive() external payable {
        // Accept ETH deposits for gas and operations
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        // Accept ETH deposits
    }
}

contracts/AtomArbitrage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// AAVE V3 Flash Loan Interface
interface IPoolAddressesProvider {
    function getPool() external view returns (address);
}

interface IPool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// Uniswap V2 Interface
interface IUniswapV2Router {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

// Balancer V2 Interfaces
interface IVault {
    struct SingleSwap {
        bytes32 poolId;
        SwapKind kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    enum SwapKind { GIVEN_IN, GIVEN_OUT }

    function swap(
        SingleSwap memory singleSwap,
        FundManagement memory funds,
        uint256 limit,
        uint256 deadline
    ) external returns (uint256);

    function querySwap(
        SingleSwap memory singleSwap,
        FundManagement memory funds
    ) external returns (uint256);

    function flashLoan(
        address recipient,
        address[] memory tokens,
        uint256[] memory amounts,
        bytes memory userData
    ) external;
}

interface IFlashLoanRecipient {
    function receiveFlashLoan(
        address[] memory tokens,
        uint256[] memory amounts,
        uint256[] memory feeAmounts,
        bytes memory userData
    ) external;
}

// Curve Finance Interfaces
interface ICurvePool {
    function exchange(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);

    function get_dy(
        int128 i,
        int128 j,
        uint256 dx
    ) external view returns (uint256);

    function coins(uint256 i) external view returns (address);
}

interface ICurveRegistry {
    function find_pool_for_coins(
        address from,
        address to
    ) external view returns (address);

    function get_coin_indices(
        address pool,
        address from,
        address to
    ) external view returns (int128, int128, bool);
}

contract AtomArbitrage is IFlashLoanSimpleReceiver, IFlashLoanRecipient, ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // AAVE V3 Pool Addresses Provider (Mainnet)
    IPoolAddressesProvider public constant ADDRESSES_PROVIDER =
        IPoolAddressesProvider(0x2f39d218133AFaB8F2B819B1066c7E434Ad94E9e);

    // Balancer V2 Vault (Mainnet)
    IVault public constant BALANCER_VAULT =
        IVault(0xBA12222222228d8Ba445958a75a0704d566BF2C8);

    // Curve Registry (Mainnet)
    ICurveRegistry public constant CURVE_REGISTRY =
        ICurveRegistry(0x90E00ACe148ca3b23Ac1bC8C240C2a7Dd9c2d7f5);

    // Uniswap V2 Router
    IUniswapV2Router public constant UNISWAP_ROUTER =
        IUniswapV2Router(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    // SushiSwap Router (same interface as Uniswap V2)
    IUniswapV2Router public constant SUSHISWAP_ROUTER =
        IUniswapV2Router(0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F);

    // Constants for $10M flash loans and 1% profit targeting
    uint256 public constant MAX_FLASH_LOAN_AMOUNT = 10_000_000 * 1e18; // $10M in 18 decimals
    uint256 public constant MIN_PROFIT_BASIS_POINTS = 100; // 1% minimum profit
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MAX_GAS_COST_USD = 50; // Maximum $50 gas cost
    uint256 public constant ETH_PRICE_USD = 2000; // Approximate ETH price for gas calculations

    enum DEX { UNISWAP, SUSHISWAP, BALANCER, CURVE }

    struct ArbitrageParams {
        address tokenA;
        address tokenB;
        uint256 amountIn;
        DEX buyDex;
        DEX sellDex;
        bytes buyData;
        bytes sellData;
        uint256 minProfit;
        uint256 maxGasPrice;
        uint256 estimatedGasUnits;
    }

    struct BalancerSwapData {
        bytes32 poolId;
        address[] path;
    }

    struct CurveSwapData {
        address pool;
        int128 i;
        int128 j;
    }

    event ArbitrageExecuted(
        address indexed tokenA,
        address indexed tokenB,
        uint256 amountIn,
        uint256 profit,
        DEX buyDex,
        DEX sellDex,
        address indexed executor
    );

    event FlashLoanExecuted(
        address indexed asset,
        uint256 amount,
        uint256 premium,
        bool success
    );

    event ProfitExtracted(
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    constructor() Ownable(msg.sender) {}

    receive() external payable {}

    /**
     * @dev Modifier to ensure gas costs don't exceed $50
     */
    modifier gasProtection(uint256 estimatedGasUnits, uint256 maxGasPrice) {
        // Calculate maximum allowed gas price to stay under $50
        uint256 maxAllowedGasPrice = (MAX_GAS_COST_USD * 1e18) / (estimatedGasUnits * ETH_PRICE_USD);
        require(tx.gasprice <= maxAllowedGasPrice, "Gas price exceeds $50 limit");
        require(tx.gasprice <= maxGasPrice, "Gas price exceeds user limit");
        _;
    }

    /**
     * @dev Execute arbitrage using AAVE flash loan (up to $10M)
     * @param asset The asset to flash loan
     * @param amount The amount to flash loan (max $10M equivalent)
     * @param params Encoded arbitrage parameters
     */
    function executeArbitrage(
        address asset,
        uint256 amount,
        bytes calldata params
    ) external onlyOwner nonReentrant {
        require(amount <= MAX_FLASH_LOAN_AMOUNT, "Amount exceeds $10M limit");

        // Decode params to check gas limits
        ArbitrageParams memory arbParams = abi.decode(params, (ArbitrageParams));

        // Apply gas protection
        uint256 maxAllowedGasPrice = (MAX_GAS_COST_USD * 1e18) / (arbParams.estimatedGasUnits * ETH_PRICE_USD);
        require(tx.gasprice <= maxAllowedGasPrice, "Gas price exceeds $50 limit");
        require(tx.gasprice <= arbParams.maxGasPrice, "Gas price exceeds user limit");

        IPool pool = IPool(ADDRESSES_PROVIDER.getPool());
        pool.flashLoanSimple(address(this), asset, amount, params, 0);
    }

    /**
     * @dev Execute arbitrage using Balancer flash loan (fee-free for certain tokens)
     * @param tokens Array of tokens to flash loan
     * @param amounts Array of amounts to flash loan
     * @param params Encoded arbitrage parameters
     */
    function executeBalancerFlashLoan(
        address[] calldata tokens,
        uint256[] calldata amounts,
        bytes calldata params
    ) external onlyOwner nonReentrant {
        for (uint i = 0; i < amounts.length; i++) {
            require(amounts[i] <= MAX_FLASH_LOAN_AMOUNT, "Amount exceeds $10M limit");
        }

        BALANCER_VAULT.flashLoan(address(this), tokens, amounts, params);
    }

    /**
     * @dev AAVE flash loan callback
     */
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        require(msg.sender == ADDRESSES_PROVIDER.getPool(), "Invalid caller");
        require(initiator == address(this), "Invalid initiator");

        // Decode arbitrage parameters
        ArbitrageParams memory arbParams = abi.decode(params, (ArbitrageParams));

        bool success = _performMultiDexArbitrage(asset, amount, arbParams);

        // Ensure minimum 1% profit after flash loan fee
        uint256 totalDebt = amount + premium;
        uint256 balance = IERC20(asset).balanceOf(address(this));
        require(balance >= totalDebt, "Insufficient balance to repay");

        uint256 profit = balance - totalDebt;
        uint256 minRequiredProfit = (amount * MIN_PROFIT_BASIS_POINTS) / BASIS_POINTS;
        require(profit >= minRequiredProfit, "Profit below 1% threshold");

        // Repay flash loan
        IERC20(asset).safeTransfer(msg.sender, totalDebt);

        emit FlashLoanExecuted(asset, amount, premium, success);
        emit ProfitExtracted(asset, profit, block.timestamp);

        return true;
    }

    /**
     * @dev Balancer flash loan callback
     */
    function receiveFlashLoan(
        address[] memory tokens,
        uint256[] memory amounts,
        uint256[] memory, /* feeAmounts - Balancer flash loans are fee-free */
        bytes memory userData
    ) external override {
        require(msg.sender == address(BALANCER_VAULT), "Invalid caller");

        // Decode arbitrage parameters
        ArbitrageParams memory arbParams = abi.decode(userData, (ArbitrageParams));

        // Execute arbitrage for the first token (can be extended for multi-token arbitrage)
        bool success = _performMultiDexArbitrage(tokens[0], amounts[0], arbParams);
        require(success, "Arbitrage execution failed");

        // Ensure minimum 1% profit (no fees for Balancer flash loans)
        uint256 balance = IERC20(tokens[0]).balanceOf(address(this));
        require(balance >= amounts[0], "Insufficient balance to repay");

        uint256 profit = balance - amounts[0];
        uint256 minRequiredProfit = (amounts[0] * MIN_PROFIT_BASIS_POINTS) / BASIS_POINTS;
        require(profit >= minRequiredProfit, "Profit below 1% threshold");

        // Repay flash loan (transfer back to Balancer Vault)
        for (uint i = 0; i < tokens.length; i++) {
            IERC20(tokens[i]).safeTransfer(address(BALANCER_VAULT), amounts[i]);
        }

        emit ProfitExtracted(tokens[0], profit, block.timestamp);
    }

    /**
     * @dev Internal function to perform multi-DEX arbitrage
     */
    function _performMultiDexArbitrage(
        address asset,
        uint256 amount,
        ArbitrageParams memory params
    ) internal returns (bool) {
        // Step 1: Buy on first DEX
        uint256 tokensBought = _executeBuy(asset, amount, params.buyDex, params.buyData);

        // Step 2: Sell on second DEX
        uint256 finalAmount = _executeSell(params.tokenB, tokensBought, params.sellDex, params.sellData);

        // Calculate profit
        uint256 profit = finalAmount > amount ? finalAmount - amount : 0;
        require(profit >= params.minProfit, "Insufficient profit");

        emit ArbitrageExecuted(
            params.tokenA,
            params.tokenB,
            amount,
            profit,
            params.buyDex,
            params.sellDex,
            owner()
        );

        return true;
    }

    /**
     * @dev Execute buy order on specified DEX
     */
    function _executeBuy(
        address tokenIn,
        uint256 amountIn,
        DEX dex,
        bytes memory data
    ) internal returns (uint256 amountOut) {
        if (dex == DEX.UNISWAP) {
            return _swapOnUniswap(tokenIn, amountIn, data, UNISWAP_ROUTER);
        } else if (dex == DEX.SUSHISWAP) {
            return _swapOnUniswap(tokenIn, amountIn, data, SUSHISWAP_ROUTER);
        } else if (dex == DEX.BALANCER) {
            return _swapOnBalancer(tokenIn, amountIn, data);
        } else if (dex == DEX.CURVE) {
            return _swapOnCurve(tokenIn, amountIn, data);
        }
        revert("Unsupported DEX");
    }

    /**
     * @dev Execute sell order on specified DEX
     */
    function _executeSell(
        address tokenIn,
        uint256 amountIn,
        DEX dex,
        bytes memory data
    ) internal returns (uint256 amountOut) {
        if (dex == DEX.UNISWAP) {
            return _swapOnUniswap(tokenIn, amountIn, data, UNISWAP_ROUTER);
        } else if (dex == DEX.SUSHISWAP) {
            return _swapOnUniswap(tokenIn, amountIn, data, SUSHISWAP_ROUTER);
        } else if (dex == DEX.BALANCER) {
            return _swapOnBalancer(tokenIn, amountIn, data);
        } else if (dex == DEX.CURVE) {
            return _swapOnCurve(tokenIn, amountIn, data);
        }
        revert("Unsupported DEX");
    }

    /**
     * @dev Swap tokens on Uniswap/SushiSwap
     */
    function _swapOnUniswap(
        address tokenIn,
        uint256 amountIn,
        bytes memory data,
        IUniswapV2Router router
    ) internal returns (uint256 amountOut) {
        address[] memory path = abi.decode(data, (address[]));

        IERC20(tokenIn).forceApprove(address(router), amountIn);

        uint256[] memory amounts = router.swapExactTokensForTokens(
            amountIn,
            0, // Accept any amount of tokens out
            path,
            address(this),
            block.timestamp + 300
        );

        return amounts[amounts.length - 1];
    }

    /**
     * @dev Swap tokens on Balancer V2
     */
    function _swapOnBalancer(
        address tokenIn,
        uint256 amountIn,
        bytes memory data
    ) internal returns (uint256 amountOut) {
        BalancerSwapData memory swapData = abi.decode(data, (BalancerSwapData));

        IERC20(tokenIn).forceApprove(address(BALANCER_VAULT), amountIn);

        IVault.SingleSwap memory singleSwap = IVault.SingleSwap({
            poolId: swapData.poolId,
            kind: IVault.SwapKind.GIVEN_IN,
            assetIn: tokenIn,
            assetOut: swapData.path[swapData.path.length - 1],
            amount: amountIn,
            userData: "0x"
        });

        IVault.FundManagement memory funds = IVault.FundManagement({
            sender: address(this),
            fromInternalBalance: false,
            recipient: payable(address(this)),
            toInternalBalance: false
        });

        return BALANCER_VAULT.swap(singleSwap, funds, 0, block.timestamp + 300);
    }

    /**
     * @dev Swap tokens on Curve
     */
    function _swapOnCurve(
        address tokenIn,
        uint256 amountIn,
        bytes memory data
    ) internal returns (uint256 amountOut) {
        CurveSwapData memory swapData = abi.decode(data, (CurveSwapData));

        IERC20(tokenIn).forceApprove(swapData.pool, amountIn);

        ICurvePool pool = ICurvePool(swapData.pool);
        return pool.exchange(swapData.i, swapData.j, amountIn, 0);
    }

    /**
     * @dev Calculate potential profit for multi-DEX arbitrage opportunity
     */
    function calculateMultiDexProfit(
        address tokenA,
        address tokenB,
        uint256 amountIn,
        DEX buyDex,
        DEX sellDex,
        bytes calldata buyData,
        bytes calldata sellData
    ) external view returns (uint256 profit, bool profitable) {
        try this._simulateArbitrage(tokenA, tokenB, amountIn, buyDex, sellDex, buyData, sellData) returns (uint256 finalAmount) {
            if (finalAmount > amountIn) {
                profit = finalAmount - amountIn;
                uint256 minRequiredProfit = (amountIn * MIN_PROFIT_BASIS_POINTS) / BASIS_POINTS;
                profitable = profit >= minRequiredProfit;
            } else {
                profit = 0;
                profitable = false;
            }
        } catch {
            profit = 0;
            profitable = false;
        }
    }

    /**
     * @dev Simulate arbitrage execution (view function)
     */
    function _simulateArbitrage(
        address tokenA,
        address tokenB,
        uint256 amountIn,
        DEX buyDex,
        DEX sellDex,
        bytes calldata buyData,
        bytes calldata sellData
    ) external view returns (uint256 finalAmount) {
        // Simulate buy on first DEX
        uint256 tokensBought = _simulateBuy(tokenA, amountIn, buyDex, buyData);

        // Simulate sell on second DEX
        finalAmount = _simulateSell(tokenB, tokensBought, sellDex, sellData);
    }

    /**
     * @dev Simulate buy order on specified DEX (view function)
     */
    function _simulateBuy(
        address, /* tokenIn */
        uint256 amountIn,
        DEX dex,
        bytes calldata data
    ) internal view returns (uint256 amountOut) {
        if (dex == DEX.UNISWAP) {
            address[] memory path = abi.decode(data, (address[]));
            uint256[] memory amounts = UNISWAP_ROUTER.getAmountsOut(amountIn, path);
            return amounts[amounts.length - 1];
        } else if (dex == DEX.SUSHISWAP) {
            address[] memory path = abi.decode(data, (address[]));
            uint256[] memory amounts = SUSHISWAP_ROUTER.getAmountsOut(amountIn, path);
            return amounts[amounts.length - 1];
        } else if (dex == DEX.BALANCER) {
            // Note: Balancer price simulation requires complex math
            // In production, implement proper Balancer weighted pool calculations
            return (amountIn * 99) / 100; // Simplified - assume 1% slippage
        } else if (dex == DEX.CURVE) {
            CurveSwapData memory swapData = abi.decode(data, (CurveSwapData));
            ICurvePool pool = ICurvePool(swapData.pool);
            return pool.get_dy(swapData.i, swapData.j, amountIn);
        }
        revert("Unsupported DEX for simulation");
    }

    /**
     * @dev Simulate sell order on specified DEX (view function)
     */
    function _simulateSell(
        address tokenIn,
        uint256 amountIn,
        DEX dex,
        bytes calldata data
    ) internal view returns (uint256 amountOut) {
        return _simulateBuy(tokenIn, amountIn, dex, data); // Same logic for simulation
    }

    /**
     * @dev Estimate gas cost in USD for arbitrage execution
     */
    function estimateGasCostUSD(
        uint256 estimatedGasUnits,
        uint256 gasPriceGwei
    ) external pure returns (uint256 gasCostUSD) {
        // Calculate gas cost in ETH
        uint256 gasCostWei = estimatedGasUnits * (gasPriceGwei * 1e9);

        // Convert to USD (using approximate ETH price)
        gasCostUSD = (gasCostWei * ETH_PRICE_USD) / 1e18;

        return gasCostUSD;
    }

    /**
     * @dev Check if gas cost is within $50 limit
     */
    function isGasCostAcceptable(
        uint256 estimatedGasUnits,
        uint256 gasPriceGwei
    ) external pure returns (bool acceptable, uint256 gasCostUSD) {
        // Calculate gas cost in ETH
        uint256 gasCostWei = estimatedGasUnits * (gasPriceGwei * 1e9);

        // Convert to USD (using approximate ETH price)
        gasCostUSD = (gasCostWei * ETH_PRICE_USD) / 1e18;

        acceptable = gasCostUSD <= MAX_GAS_COST_USD;
    }

    /**
     * @dev Get maximum allowed gas price for $50 limit
     */
    function getMaxGasPriceForLimit(
        uint256 estimatedGasUnits
    ) external pure returns (uint256 maxGasPriceGwei) {
        // Calculate max gas price in wei
        uint256 maxGasPriceWei = (MAX_GAS_COST_USD * 1e18) / (estimatedGasUnits * ETH_PRICE_USD);

        // Convert to gwei
        maxGasPriceGwei = maxGasPriceWei / 1e9;

        return maxGasPriceGwei;
    }

    /**
     * @dev Emergency withdraw function
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    /**
     * @dev Withdraw ETH
     */
    function withdrawETH() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * @dev Withdraw ERC20 tokens
     */
    function withdrawToken(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }
}

contracts/BaseAtomArbitrage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// AAVE V3 Flash Loan Interface
interface IPoolAddressesProvider {
    function getPool() external view returns (address);
}

interface IPool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// Uniswap V2 Interface
interface IUniswapV2Router {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

// Balancer V2 Interfaces
interface IVault {
    struct SingleSwap {
        bytes32 poolId;
        SwapKind kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    enum SwapKind { GIVEN_IN, GIVEN_OUT }

    function swap(
        SingleSwap memory singleSwap,
        FundManagement memory funds,
        uint256 limit,
        uint256 deadline
    ) external returns (uint256);

    function querySwap(
        SingleSwap memory singleSwap,
        FundManagement memory funds
    ) external returns (uint256);

    function flashLoan(
        address recipient,
        address[] memory tokens,
        uint256[] memory amounts,
        bytes memory userData
    ) external;
}

interface IFlashLoanRecipient {
    function receiveFlashLoan(
        address[] memory tokens,
        uint256[] memory amounts,
        uint256[] memory feeAmounts,
        bytes memory userData
    ) external;
}

// Curve Finance Interfaces
interface ICurvePool {
    function exchange(
        int128 i,
        int128 j,
        uint256 dx,
        uint256 min_dy
    ) external returns (uint256);

    function get_dy(
        int128 i,
        int128 j,
        uint256 dx
    ) external view returns (uint256);

    function coins(uint256 i) external view returns (address);
}

interface ICurveRegistry {
    function find_pool_for_coins(
        address from,
        address to
    ) external view returns (address);

    function get_coin_indices(
        address pool,
        address from,
        address to
    ) external view returns (int128, int128, bool);
}

contract BaseAtomArbitrage is IFlashLoanSimpleReceiver, IFlashLoanRecipient, ReentrancyGuard, Ownable {
